package com.priyanshi.EmployeeManagementCRUD.controller;import com.priyanshi.EmployeeManagementCRUD.DTO.DepartmentDTO;import com.priyanshi.EmployeeManagementCRUD.entity.Department;import com.priyanshi.EmployeeManagementCRUD.service.DepartmentService;import com.priyanshi.EmployeeManagementCRUD.service.EmployeeService;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping(value="/departments")public class DepartmentController {    private final DepartmentService departmentService;    public DepartmentController(DepartmentService departmentService){        this.departmentService=departmentService;    }    @PostMapping(path="/emp")    public Department createDepartment(@RequestBody DepartmentDTO dto){        return departmentService.createDepartment(dto);    }    @GetMapping    public List<Department> getAll(){        return departmentService.getAllDepartment();    }}